// Get the transcription text and original webhook data
const whisperResult = $input.item.json;
const webhookData = $('Webhook').item.json.body;

// Extract transcription
const transcriptionText = whisperResult.text || whisperResult.transcription || 'No transcription available';

// Pass all data forward
return {
  // From webhook body
  transcriptionId: webhookData.uploadId,
  doctorName: webhookData.doctorName,
  patientName: webhookData.patientName,
  documentType: webhookData.documentType,
  fileName: webhookData.fileName,
  audioUrl: webhookData.audioUrl,
  callbackUrl: webhookData.callbackUrl,

  // Transcription
  rawTranscription: transcriptionText
};

// Get Agent response and original data
const agentResponse = $input.item.json;
const originalData = $('Prepare for Gemini').item.json;

// Try to extract formatted text from different possible shapes
let formattedText = '';

if (typeof agentResponse === 'string') {
  formattedText = agentResponse;
} else if (agentResponse?.text && typeof agentResponse.text === 'string') {
  formattedText = agentResponse.text;
} else if (agentResponse?.output && typeof agentResponse.output === 'string') {
  formattedText = agentResponse.output;
} else if (agentResponse?.message?.content && Array.isArray(agentResponse.message.content)) {
  const part = agentResponse.message.content[0];
  if (part?.text) formattedText = part.text;
}

// Fallback to raw transcription if we couldn't get formatted text
if (!formattedText) {
  formattedText = originalData.rawTranscription || '';
}

// Add header and footer to create a complete formatted document
const finalDocument = `MEDICAL TRANSCRIPTION REPORT
================================================================================
Date: ${new Date().toLocaleDateString()} ${new Date().toLocaleTimeString()}
Document Type: ${(originalData.documentType || '').toUpperCase()}
Doctor: ${originalData.doctorName || 'Unknown'}
Patient: ${originalData.patientName || 'Unknown'}
File: ${originalData.fileName || ''}
Transcription ID: ${originalData.transcriptionId || ''}
================================================================================

${formattedText}

================================================================================
END OF MEDICAL RECORD
Transcribed and formatted by AI Medical Transcription System
================================================================================`;

// Combine raw and formatted into a single transcription_text field
const combinedTranscription = `=== RAW TRANSCRIPTION ===\n${originalData.rawTranscription || ''}\n\n=== FORMATTED DOCUMENT ===\n${finalDocument}`;

return {
  transcriptionId: originalData.transcriptionId,
  transcription_text: combinedTranscription,
  doctor_name: originalData.doctorName,
  patient_name: originalData.patientName,
  document_type: originalData.documentType,
  fileName: originalData.fileName,
  audioUrl: originalData.audioUrl,
  callbackUrl: originalData.callbackUrl,
  status: 'completed',
  processed_at: new Date().toISOString(),
  formatted_document_for_response: finalDocument,
  raw_transcription_for_response: originalData.rawTranscription
};

parameters.text:
={{ `You are a professional medical transcriptionist.

Your task: Format the raw transcription into a clean, professional ${$json.documentType || 'clinical'} document.

Document information:
- Type: ${$json.documentType || 'Unknown'}
- Doctor: ${$json.doctorName || 'Unknown'}
- Patient: ${$json.patientName || 'Unknown'}
- Date: ${new Date().toLocaleDateString()}
- File: ${$json.fileName || ''}
- Transcription ID: ${$json.transcriptionId || ''}

Raw transcription:
${$json.rawTranscription}

Instructions:
1) Correct medical terminology and grammar
2) Use appropriate section headers for the document type
3) Mark unclear sections with [?]
4) If documentType is 'surgery', include: Preoperative Diagnosis, Postoperative Diagnosis, Procedure, Findings, Technique
5) If documentType is 'consultation', include: Chief Complaint, History, Examination, Assessment, Plan

Output requirements:
- Return ONLY the formatted document content.
- Do not add any explanations or code fences.
` }}

