// Process incoming webhook data with CORRECT field names
const item = $input.item.json;

console.log('Received webhook data');
console.log('Upload ID:', item.uploadId);
console.log('Audio URL:', item.audioUrl);
console.log('Doctor:', item.doctorName);
console.log('Patient:', item.patientName);

// Map the actual webhook fields to what we need
const processedData = {
  // Use uploadId from webhook
  transcriptionId: item.uploadId || item.id || '',

  // Audio fields - NO base64 audio is sent, only URL
  audioUrl: item.audioUrl || '',
  hasAudioUrl: !!item.audioUrl,

  // File information
  fileName: item.fileName || 'audio.mp3',
  fileSize: item.fileSize || 0,
  fileType: item.fileType || 'audio/mpeg',

  // Medical information - these are coming through correctly
  doctorName: item.doctorName || '',
  patientName: item.patientName || '',
  documentType: item.documentType || 'consultation',

  // Other fields
  uploadTime: item.uploadTime || new Date().toISOString(),
  source: item.source || 'dashboard-next',
  callbackUrl: item.callbackUrl || 'http://localhost:3000/api/transcription-result-v2',

  // For workflow routing
  needsDownload: !!item.audioUrl && !item.audio
};

console.log('Processed data:', {
  transcriptionId: processedData.transcriptionId,
  hasAudioUrl: processedData.hasAudioUrl,
  needsDownload: processedData.needsDownload
});

return processedData;

// Merge transcription results from either real or mock source
const item = $input.item.json;
const originalData = $('Process Data').item.json;
let rawTranscription = '';
let source = 'unknown';

// Check if we have real transcription from Whisper
if (item.text) {
  // Direct text response from Whisper
  rawTranscription = item.text;
  source = 'whisper';
} else if (item.transcription) {
  // Alternative field name
  rawTranscription = item.transcription;
  source = 'whisper';
} else if (item.data && typeof item.data === 'string') {
  // Sometimes the response is in data field
  rawTranscription = item.data;
  source = 'whisper';
} else if (item.rawTranscription) {
  // Mock transcription
  rawTranscription = item.rawTranscription;
  source = item.source || 'mock';
} else {
  // Fallback
  rawTranscription = 'Transcription not available. Please check the audio file.';
  source = 'error';
}

console.log('Transcription source:', source);
console.log('Transcription length:', rawTranscription.length);

// Prepare complete data for next steps
return {
  rawTranscription: rawTranscription,
  transcriptionId: originalData.transcriptionId,
  doctorName: originalData.doctorName,
  patientName: originalData.patientName,
  documentType: originalData.documentType,
  fileName: originalData.fileName,
  audioUrl: originalData.audioUrl,
  callbackUrl: originalData.callbackUrl,
  source: source
};

