// Extract formatted document from Gemini response
const geminiResponse = $input.item.json;
const originalData = $('Merge Results').item.json;

let formattedText = '';

// Parse Gemini API response structure
try {
  if (geminiResponse.candidates && geminiResponse.candidates[0]) {
    if (geminiResponse.candidates[0].content && geminiResponse.candidates[0].content.parts) {
      formattedText = geminiResponse.candidates[0].content.parts[0].text;
    }
  } else if (typeof geminiResponse === 'string') {
    formattedText = geminiResponse;
  }
} catch (error) {
  console.error('Error parsing Gemini response:', error);
  formattedText = originalData.rawTranscription;
}

if (!formattedText) {
  formattedText = originalData.rawTranscription;
}

// Create final document with header and footer
const currentDate = new Date().toLocaleDateString();
const currentTime = new Date().toLocaleTimeString();

const finalDocument = `MEDICAL TRANSCRIPTION REPORT
================================================================================
Generated: ${currentDate} at ${currentTime}
Document Type: ${originalData.documentType.toUpperCase().replace(/_/g, ' ')}
Healthcare Provider: ${originalData.doctorName}
Patient Name: ${originalData.patientName}
Transcription ID: ${originalData.transcriptionId}
File: ${originalData.fileName}
Source: ${originalData.source === 'mock' ? 'Test Data' : 'Audio Transcription'}
================================================================================

${formattedText}

================================================================================
END OF MEDICAL RECORD
${originalData.source === 'mock' ? 'Note: This is test data for workflow validation' : 'This document was transcribed using AI-assisted technology'}
Processed by: Medical Transcription System v12
================================================================================`;

console.log('Document formatted successfully');

// Return complete data for database update
return {
  transcriptionId: originalData.transcriptionId,
  transcription_text: originalData.rawTranscription,
  formatted_document: finalDocument,
  doctor_name: originalData.doctorName,
  patient_name: originalData.patientName,
  document_type: originalData.documentType,
  fileName: originalData.fileName,
  audioUrl: originalData.audioUrl,
  callbackUrl: originalData.callbackUrl,
  status: 'completed',
  processed_at: new Date().toISOString(),
  source: originalData.source
};

