// Normalize payload; do NOT include base64 in v2
const data = $input.first().json;
return [{ json: data }];

// Ensure binary property name is 'file' for OpenAI
const item = $input.first();
return [{ json: item.json, binary: item.binary }];

// Fix data flow: Get OpenAI response and reconstruct full payload
const oai = $input.first().json || {};
const text = oai.text || 'Transcription pending: external STT not configured.';

// Get original data from the webhook via the execution data
const webhookData = $('Webhook').first().json || {};

// Generate UUID if uploadId is not a valid UUID format
function generateUUID() {
  return 'xxxxxxxx-xxxx-4xxx-yxxx-xxxxxxxxxxxx'.replace(/[xy]/g, function(c) {
    const r = Math.random() * 16 | 0;
    const v = c == 'x' ? r : (r & 0x3 | 0x8);
    return v.toString(16);
  });
}

// Use uploadId from webhook, or generate UUID if it's not UUID format
const uploadId = webhookData.uploadId && /^[0-9a-f]{8}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{4}-[0-9a-f]{12}$/i.test(webhookData.uploadId) 
  ? webhookData.uploadId 
  : generateUUID();

console.log('Debug - Original uploadId:', webhookData.uploadId);
console.log('Debug - Final uploadId (UUID):', uploadId);
console.log('Debug - OpenAI response:', JSON.stringify(oai));

// Return complete data with transcription
return [{ 
  json: { 
    uploadId: uploadId,
    fileName: webhookData.fileName || 'hardcoded-test.mp3', 
    doctorName: webhookData.doctorName || 'Dr Hardcoded',
    patientName: webhookData.patientName || 'Values Success',
    documentType: webhookData.documentType || 'final-victory',
    audioUrl: webhookData.audioUrl || 'https://example.com/hardcoded-test.mp3',
    userId: webhookData.userId || null,
    fileSize: webhookData.fileSize || 0,
    callbackUrl: webhookData.callbackUrl || null,
    transcription_text: text
  } 
}];
